import { canvas, ctx } from './canvas.js';
import { getCoords, frame } from './function.js';

/**
 * Coords
 */

// ball start coords
let ballX = canvas.width/2 + 50;
let ballY = 50;
// ball speed
let newBallX = -0;
let newBallY = 3;
// board start coords
let boardX = (canvas.width-75)/2;
let boardY = canvas.height-20;
/**
 * Draw
 */
// board
function board() {
    ctx.beginPath();
    ctx.rect(boardX, boardY, 75, 10);
    ctx.fillStyle = "#0095DD";
    ctx.fill();
    ctx.closePath();
}
// ball
function ball() {
    ctx.beginPath();
    ctx.arc(ballX, ballY, 10, 0, Math.PI*2);
    ctx.fillStyle = "#0095DD";
    ctx.fill();
    ctx.closePath();
}
//bricks
let bricksGeo = [];
for(let i = 0; i < 8; i++) {
    bricksGeo[i] = [];
    for(let j = 0; j < 4; j++) {
        bricksGeo[i][j] = { x: 0, y: 0};
    }
}
function bricks() {
    for( let i = 0 ; i < 8 ; i++){
        for( let j = 0 ; j < 4 ; j++){
            
                window.brickX = (i * (100 + 20)) + 490;
                window.brickY = (j * (100 + 20)) + 100;
                bricksGeo[i][j].x = brickX;
                bricksGeo[i][j].y = brickY;
                ctx.beginPath();
                ctx.rect(brickX, brickY, 100, 40);
                ctx.fillStyle = "#0095DD";
                ctx.fill();
                ctx.closePath();
             
        }
    }
}
// bricks collisions
function collisions() {
    for(let i = 0; i < 8; i++) {
        for(let j = 0; j < 4; j++) {
            let b = bricksGeo[i][j];
            // Collisions ici, mais j'y arrive pas 
                // if(ballY + newBallY + 5 <= b.y + 40 && ballX + newBallX + 5 > b.x && ballX + newBallX + 5 < b.x + 100 ){
                //     newBallY = -newBallY 
                // }
                // if(ballY + newBallY + 5 >= b.y - 2 && ballX + newBallX + 5 > b.x && ballX + newBallX + 5 < b.x + 100 ){
                //     newBallY = -newBallY
                // } 
                // if(ballY + newBallY >= brickY + (40/2)){
                //     newBallY = -newBallY
                // }
                // if(ballX + newBallX < brickX){
                //     newBallX = -newBallX
                // }
                // if(ballX + newBallX > brickX){
                //     newBallX = -newBallX
                // }
            
        }
    }
}


/**
 * bouger la planche avec le clavier
 */
//init touche
const keyboard = {}
keyboard.left = false
keyboard.right = false

document.addEventListener('keydown', (_event) =>
{
    //console.log(_event.code)

    switch(_event.code)
    {
        case 'ArrowRight':
            keyboard.right = true
    }
    switch(_event.code)
    {
        case 'ArrowLeft':
            keyboard.left = true
    }
})

document.addEventListener('keyup', (_event) =>
{
    //console.log(_event.code)

    switch(_event.code)
    {
        case 'ArrowRight':
            keyboard.right = false
    }
    switch(_event.code)
    {
        case 'ArrowLeft':
            keyboard.left = false
    }
})



// draw ( loop )
function draw() {
    ctx.clearRect(0, 0, canvas.width, canvas.height);
    ball();
    board();
    bricks();
    collisions();
    ballX += newBallX;
    ballY += newBallY;
    if(ballY + newBallY - 5 < 0) {
        newBallY = -newBallY;
    }
    if(ballX + newBallX + 5 > canvas.width) {
        newBallX = -newBallX;
    }
    if(ballY + newBallY + 5 > canvas.height) {
        newBallY = -newBallY
    }
    if(ballX + newBallX - 5 < 0) {
        newBallX = -newBallX;
    }
    if( ballY + newBallY + 5 > boardY && ballX + newBallX > boardX && ballX + newBallX < boardX + 75){
        newBallY = -newBallY
    }

    /**
     * tentative de controle direction avec le clavier
     */
    if(keyboard.left == false && keyboard.right == true){
        boardX += 5 
    }

    if(keyboard.left == true && keyboard.right == false){
        boardX += -5 
    }



    /**
     * detection pinch + direction ( a mettre dans le draw)
     */
    console.log(frame.hands[0])
    //console.log(frame.hands[0].pinchStrength)
    //console.log(frame.hands[0].palmPosition[0])
    if(frame.hands[0].pinchStrength > 0.91){
        boardX = frame.hands[0].palmPosition[0]*3 + (canvas.height + 75) ;
    }
}
setInterval (draw, 10)
